#!/bin/sh

PASSED_ARGS="${@}"
CLASS_NAME="com.kas.mq.server.KasMqServer"

#::::::::::::::::::::::::::::::::::::::::::::::::::::::::::::::::::::::::::::::::::::::::::::::::::::::::::::::
# Setup
#::::::::::::::::::::::::::::::::::::::::::::::::::::::::::::::::::::::::::::::::::::::::::::::::::::::::::::::
SCRIPT_DIR=`dirname ${0}`
. ${SCRIPT_DIR}/setup.sh

#::::::::::::::::::::::::::::::::::::::::::::::::::::::::::::::::::::::::::::::::::::::::::::::::::::::::::::::
# Run command
#::::::::::::::::::::::::::::::::::::::::::::::::::::::::::::::::::::::::::::::::::::::::::::::::::::::::::::::
${JAVA_EXEC} ${DEBUG_OPTS} -classpath ${CLASS_PATH} com.kas.appl.AppLauncher \
  kas.class=${CLASS_NAME} \
  ${PASSED_ARGS}
